(83, 'Quality Policy', 'quality-policy', 'Quality Policy', 'Main', 'Normal', 3, NULL, '<br />\r\nWe are dedicated to provide highest quality service to our students ensuring highest standard of professional performance through feedback systems and other quality assurance tools. We assure that information delivered to our students regarding abroad study is extracted from authentic source of information.<br />\r\n<br />\r\nWe strictly follow the rules and regulations, and guidelines for the quality education by Ministry of Education (MOE) Nepal. Additionally, as an authorized international student recruiter for the universities or colleges with whom we are working, we are committed to follow the Memorandum of Understanding (MoU). We take rules and regulations, and guidelines not as restriction to execute our services but to enhance and maintain the quality.', 2, NULL, NULL, '1549277872_quality-policy-banner-2.jpg', NULL, NULL, NULL, 'Quality Policy', NULL, '1', '2019-01-30 16:06:39', '2019-02-04 16:57:52'),
(84, 'Instagram', 'instagram', 'Instagram', 'SNS', 'Link', 4, NULL, NULL, 0, NULL, NULL, NULL, 'https://www.instagram.com/easylinkcareer/', NULL, NULL, 'Instagram', NULL, '1', '2019-02-03 15:03:34', '2019-02-03 15:03:34'),
(85, 'Rashmila Hayu', 'rashmila-hayu', 'Australia - Sydney', 'Others', 'Normal', 3, '\"Right educational counseling is most important because there is no editing or correcting tool for mistakes in life. So, landing students to the right educational destination do matter for our career and you could find the right educational counseling and professionals in abroad study.\"', NULL, 50, '1549966602_Rasmila Hayu.jpg', NULL, NULL, NULL, NULL, NULL, 'Rashmila Hayu', NULL, '1', '2019-02-12 16:16:42', '2019-02-12 16:45:25'),
(86, 'Uma Dhungel', 'uma-dhungel', 'Australia - Sydney', 'Others', 'Normal', 4, '\"The puzzle of further career path is most crucial after grade 12 for most of students like me. We might turn our career path according to our friends or circles we have which might sometime leads us to regret. So, good educational counseling and guidance matters which you could find at EasyLink Career Consultant with team of experts.\"<br />\r\n&nbsp;', NULL, 50, '1549967859_UmaDungel.jpg', NULL, NULL, NULL, NULL, NULL, 'Uma Dhungel', NULL, '1', '2019-02-12 16:37:39', '2019-02-12 16:45:51');

